import React, { Component } from "react"

export default class Resposta extends Component {
    render() {
        return (
            <div className="resposta">
                <table className="table">
                    <tbody>
                        <tr>
                            <td>{this.props.descricao}</td>
                            <td>Votos: {this.props.votos}</td>                
                            {this.props.remover &&
                            <td><button className="btn btn-danger" onClick={this.props.remover}>Remover</button></td>
                            }
                        </tr>
                    </tbody>
                </table>
            </div>
        )
    }
}
