import React, { Component } from "react"
import Resposta from "../../components/Resposta"

import EnqueteService from "../../services/enquete-service"

export default class CriarEnquetePage extends Component {
    constructor(props) {
        super(props)

        this.state = {
            titulo: "",
            inicio: "",
            fim: "",
            descricao: "",
            respostas: []
        }
    }

    onSave = () => {
        EnqueteService.criar(this.state)
            .then(e => console.log(e))
    }

    onChangeTitulo = (e) => {
        this.setState({ titulo: e.target.value })
    }

    onChangeInicio = (e) => {
        this.setState({ inicio: e.target.value })
    }

    onChangeFim = (e) => {
        this.setState({ fim: e.target.value })
    }

    adicionarResposta = () => {
        const copyRespostas = Object.assign([], this.state.respostas)
        copyRespostas.push({
            descricao: this.state.descricao
        })
        this.setState({ respostas: copyRespostas })
    }

    render() {
        return (
            <div>
                <label htmlFor="titulo">Título</label>
                <input name="titulo" id="titulo" value={this.state.titulo} onChange={this.onChangeTitulo}/> 
                <label htmlFor="inicio">Data de início</label>
                <input type="date" name="inicio" id="inicio" value={this.state.inicio} onChange={this.onChangeInicio}/>
                <label htmlFor="fim">Data de término</label>
                <input type="date" name="fim" id="fim" value={this.state.fim} onChange={this.onChangeFim}/>
                <div>
                    {this.state.respostas.map((resposta, index) => {
                        return (
                            <Resposta descricao={resposta.descricao}/>
                        )
                    })}
                </div>
                <button type="submit" onClick={this.onSave}>Criar</button>
            </div>
        )
    }
}
