import http from "../api/votacao"

class EnqueteService {

    atualizar(enquete) {
        return http.put("/", enquete)
    }

    async buscar(id) {
        console.log("ASDDS")
        return await http({method: 'get', url: '/?id=12'})
    }

    buscarTodos() {
        return http.get("/")
    }

    criar(enquete) {
        return http.post("/", enquete)
    }

    remover(id) {
        return http.delete(`/?id=${id}`)
    }
}

export default new EnqueteService()
