import React, { Component } from "react"

import EnqueteService from "../services/enquete-service"

export default class Enquete extends Component {
    constructor(props) {
        super(props)

        this.state = {
            reload: false
        }
    }

    onRemove = () => {
        EnqueteService.remover(this.props.enquete.id)
            .then(this.setState({ reload: true}))
    }

    onEdit = () => {
        this.props.history.push(`/edit/${this.props.enquete.id}`)
    }

    render() {
        const enquete = this.props.enquete

        return (
            <tr>
                <td>{enquete.titulo}</td>
                <td>{new Date(enquete.inicio).toLocaleDateString()}</td>
                <td>{new Date(enquete.fim).toLocaleDateString()}</td>
                <td><button onClick={this.onEdit}>Editar</button></td>
                <td><button onClick={this.onRemove}>Remover</button></td>
            </tr>
        )
    }
}
