import React, { Component } from "react"

export default class Resposta extends Component {
    render() {
        return (
            <div>
                <div className="resposta">
                    <div>{this.props.descricao}</div>
                    <div> Votos: {this.props.votos}</div>
                </div>
                {this.props.remover &&
                <button className="btn btn-danger" onClick={this.props.remover}>Remover</button>
                }
            </div>
        )
    }
}
