import React, { Component } from "react"

import EnqueteService from "../services/enquete-service"

export default class Enquete extends Component {
    constructor(props) {
        super(props)

        this.state = {
            reload: false
        }
    }

    onRemove = () => {
        EnqueteService.remover(this.props.enquete.id)
            .then(() => window.location.reload(false))
    }

    onEdit = () => {
        this.props.history.push(`/edit/${this.props.enquete.id}`)
    }

    onShow = () => {
        this.props.history.push(`/show/${enquete.id}`)
    }

    getLocalDate = (date) => {
        return new Date(date).toLocaleDateString('br', { timeZone: "UTC" })
    }

    render() {
        const enquete = this.props.enquete

        return (
            <tr>
                <td onClick={this.onShow}>{enquete.titulo}</td>
                <td>{this.getLocalDate(enquete.inicio)}</td>
                <td>{this.getLocalDate(enquete.fim)}</td>
                <td><button onClick={this.onEdit} className="btn btn-warning">Editar</button></td>
                <td><button onClick={this.onRemove} className="btn btn-danger">Remover</button></td>
            </tr>
        )
    }
}
