import React, { Component } from "react"
import Resposta from "../../components/Resposta"

import EnqueteService from "../../services/enquete-service"

export default class MostrarEnquetePage extends Component {

    readOnly = false

    constructor(props) {
        super(props)

        this.state = {
            id: 0,
            titulo: "",
            inicio: "",
            fim: "",
            respostas: []
        }
    }

    componentDidMount() {
        if (this.props.readOnly) {
            this.readOnly = true
        }
        EnqueteService.buscar(this.props.match.params.id)
            .then(response => response.data)
            .then(enquete => this.setState({
                id: enquete.enquete.id,
                titulo: enquete.enquete.titulo,
                inicio: enquete.enquete.inicio,
                fim: enquete.enquete.fim,
                respostas: enquete.respostas
            }))
            .catch(console.log)
    }

    onRemove = () => {
        EnqueteService.remover(this.state.id)
            .then(this.props.history.push("/"))
            .catch(console.log)
    }

    onEdit = () => {
        this.props.history.push(`/edit/${this.state.id}`)
    }

    render() {
        return (
            <div className="container">
                <label htmlFor="titulo">Título</label>
                <input name="titulo" id="titulo" className="form-control" readOnly value={this.state.titulo}/> 
                <label htmlFor="inicio">Data de início</label>
                <input type="date" name="inicio" id="inicio" className="form-control" readOnly value={this.state.inicio}/>
                <label htmlFor="fim">Data de término</label>
                <input type="date" name="fim" id="fim" className="form-control" readOnly value={this.state.fim}/>
                <div className="respostas">
                    <label htmlFor="descricao">Opções de resposta</label>
                    <div>
                        {this.state.respostas.map((resposta, index) =>
                            <Resposta key={index} descricao={resposta.descricao} votos={resposta.votos}/>
                        )}
                    </div>
                </div>
                <td><button onClick={this.onEdit} className="btn btn-warning">Editar</button></td>
                <td><button onClick={this.onRemove} className="btn btn-danger">Remover</button></td>
            </div>
        )
    }
}
