import React, { Component } from "react"
import Resposta from "../../components/Resposta"

import EnqueteService from "../../services/enquete-service"
import { validate } from "../../validate/enquete-validate"

export default class EditarEnquetePage extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: 0,
            titulo: "",
            inicio: "",
            fim: "",
            respostas: [],
            descricao: "",
            errors: {},
            validated: false
        }
    }

    componentDidMount() {
        EnqueteService.buscar(this.props.match.params.id)
            .then(response => response.data)
            .then(enquete => this.setState({
                id: enquete.enquete.id,
                titulo: enquete.enquete.titulo,
                inicio: enquete.enquete.inicio,
                fim: enquete.enquete.fim,
                respostas: enquete.respostas
            }))
            .catch(console.log)
    }

    onSave = () => {
        const { id, titulo, inicio, fim, respostas } = this.state
        const enquete = {
            id,
            titulo,
            inicio,
            fim,
            respostas
        }

        const { errors, hasError } = validate(enquete)
        this.setState({ errors })
        this.setState({ validated: true })

        if (hasError) { return }

        EnqueteService.atualizar(enquete)
            .then(this.props.history.push(`/show/${this.state.id}`))
    }

    onChangeTitulo = (e) => {
        this.setState({ titulo: e.target.value })
    }

    onChangeInicio = (e) => {
        this.setState({ inicio: e.target.value })
    }

    onChangeFim = (e) => {
        this.setState({ fim: e.target.value })
    }

    onChangeDescricao = (e) => {
        this.setState({ descricao: e.target.value })
    }

    adicionarResposta = () => {
        if (this.state.descricao.length === 0) { return }
        const copyRespostas = Object.assign([], this.state.respostas)
        copyRespostas.push({ descricao: this.state.descricao, votos: 0 })
        this.setState({ respostas: copyRespostas })
        this.setState({ descricao: "" })
    }

    removerResposta = (index) => {
        const copyRespostas = Object.assign([], this.state.respostas)
        copyRespostas.splice(index, 1)
        this.setState({ respostas: copyRespostas })
    }

    render() {
        const errors = this.state.errors
        const validated = this.state.validated
        const titulo = errors.titulo ? "is-invalid" : "is-valid"
        const inicio = errors.inicio ? "is-invalid" : "is-valid"
        const fim = errors.fim ? "is-invalid" : "is-valid"
        const respostas = errors.respostas ? "is-invalid" : "is-valid"

        return (
            <div className="container">
                <label htmlFor="titulo">Título</label>
                <div>
                    <input name="titulo" id="titulo" className={"form-control " + (validated && titulo)} value={this.state.titulo} onChange={this.onChangeTitulo}/> 
                    {errors.titulo && <div className="invalid-feedback">{errors.titulo}</div>}
                </div>
                <label htmlFor="inicio">Data de início</label>
                <div>
                    <input type="date" name="inicio" id="inicio" className={"form-control " + (validated && inicio)} value={this.state.inicio} onChange={this.onChangeInicio}/>
                    {errors.inicio && <div className="invalid-feedback">{errors.inicio}</div>}
                </div>
                <label htmlFor="fim">Data de término</label>
                <div>
                    <input type="date" name="fim" id="fim" className={"form-control " + (validated && fim)} value={this.state.fim} onChange={this.onChangeFim}/>
                    {errors.fim && <div className="invalid-feedback">{errors.fim}</div>}
                </div>
                <div className="respostas">
                    <label htmlFor="descricao">Opção de resposta</label>
                    <div>
                        <input className={"form-control " + (validated && respostas)} id="descricao" value={this.state.descricao} onChange={this.onChangeDescricao}/>
                        {errors.respostas && <div className="invalid-feedback">{errors.respostas}</div>}
                    </div>
                    <button className="btn btn-warning" onClick={this.adicionarResposta}>Adicionar Resposta</button>
                    <div>
                        {this.state.respostas.map((resposta, index) =>
                            <Resposta key={index} descricao={resposta.descricao} votos={resposta.votos} remover={this.removerResposta.bind(this, index)}/>
                        )}
                    </div>
                </div>
                <button type="submit" className="btn btn-primary" onClick={this.onSave}>Salvar</button>
            </div>
        )
    }
}
