import {api } from "../api/votacao"

class EnqueteService {

    atualizar(enquete) {
        return api.put("/", enquete)
    }

    buscar(id) {
        return api.get(`/?id=${id}`)
    }

    buscarTodos() {
        return api.get("/")
    }

    criar(enquete) {
        return api.post("/", enquete)
    }

    remover(id) {
        return api.delete(`/?id=${id}`)
    }
}

export default new EnqueteService()
