import React, { Component } from "react"
import Resposta from "../../components/Resposta"

import EnqueteService from "../../services/enquete-service"

export default class MostrarEnquetePage extends Component {
    constructor(props) {
        super(props)

        this.state = {
            titulo: "",
            inicio: "",
            fim: "",
            respostas: []
        }
    }

    componentDidMount() {
        EnqueteService.buscar(this.props.match.params.id)
            .then(response => response.data)
            .then(enquete => this.setState({
                titulo: enquete.titulo,
                inicio: enquete.inicio,
                fim: enquete.fim
            }))
    }

    render() {
        return (
            <div className="container">
                <label htmlFor="titulo">Título</label>
                <input name="titulo" id="titulo" className="form-control" value={this.state.titulo}/> 
                <label htmlFor="inicio">Data de início</label>
                <input type="date" name="inicio" id="inicio" className="form-control" value={this.state.inicio}/>
                <label htmlFor="fim">Data de término</label>
                <input type="date" name="fim" id="fim" className="form-control" value={this.state.fim}/>
                <div className="respostas">
                    <label htmlFor="descricao">Opção de resposta</label>
                    <div>
                        {this.state.respostas.map((resposta, index) =>
                            <Resposta key={index} descricao={resposta.descricao}/>
                        )}
                    </div>
                </div>
                <button type="submit" className="btn btn-primary" onClick={this.onSave}>Criar</button>
            </div>
        )
    }
}
