import React, { Component } from "react"
import Resposta from "../../components/Resposta"

import EnqueteService from "../../services/enquete-service"
import { validate } from "../../validate/enquete-validate"

export default class CriarEnquetePage extends Component {
    constructor(props) {
        super(props)

        this.state = {
            titulo: "",
            inicio: "",
            fim: "",
            descricao: "",
            respostas: [],
            errors: {},
            validated: false
        }
    }

    onSave = () => {
        const { titulo, inicio, fim } = this.state
        const enquete = {
            titulo,
            inicio,
            fim
        }

        const { errors, hasError } = validate(enquete)
        this.setState({ errors })
        this.setState({ validate: true })

        if (hasError) { return }

        EnqueteService.criar(this.state)
            .then(this.props.history.push("/"))
    }

    onChangeTitulo = (e) => {
        this.setState({ titulo: e.target.value })
    }

    onChangeInicio = (e) => {
        this.setState({ inicio: e.target.value })
    }

    onChangeFim = (e) => {
        this.setState({ fim: e.target.value })
    }

    onChangeDescricao = (e) => {
        this.setState({ descricao: e.target.value })
    }

    adicionarResposta = () => {
        const copyRespostas = Object.assign([], this.state.respostas)
        copyRespostas.push({ descricao: this.state.descricao })
        this.setState({ respostas: copyRespostas })
    }

    removerResposta = (index) => {
        const copyRespostas = Object.assign([], this.state.respostas)
        copyRespostas.splice(index, 1)
        this.setState({ respostas: copyRespostas })
    }

    render() {
        const titulo = errors.titulo ? "is-invalid" :  "is-valid"

        return (
            <div className="container">
                <label htmlFor="titulo">Título</label>
                <input name="titulo" id="titulo" className="form-control" value={this.state.titulo} onChange={this.onChangeTitulo}/> 
                <label htmlFor="inicio">Data de início</label>
                <input type="date" name="inicio" id="inicio" className="form-control" value={this.state.inicio} onChange={this.onChangeInicio}/>
                <label htmlFor="fim">Data de término</label>
                <input type="date" name="fim" id="fim" className="form-control" value={this.state.fim} onChange={this.onChangeFim}/>
                <div className="respostas">
                    <input className="form-control" onChange={this.onChangeDescricao}/>
                    <button className="btn btn-warning" onClick={this.adicionarResposta}>Adicionar Resposta</button>
                    <div>
                        {this.state.respostas.map((resposta, index) =>
                            <Resposta descricao={resposta.descricao} remover={this.removerResposta.bind(this, index)}/>
                        )}
                    </div>
                </div>
                <button type="submit" className="btn btn-primary" onClick={this.onSave}>Criar</button>
            </div>
        )
    }
}
