import React, { Component } from "react"

export default class Resposta extends Component {
    constructor(props) {
        super(props)

        this.state = {
            descricao: ""
        }
    }

    onChangeDescricao = (e) => {
        this.setState({ descricao: e.target.value })
    }

    render() {
        return (
            <div>
                <input onChange={this.onChangeDescricao}/>
                <button>Remover</button>
            </div>
        )
    }
}
