import { http } from "../api/votacao"

class EnqueteService {

    http = http

    static atualizar(enquete) {
        return http.put("/", enquete)
    }

    static buscar(id) {
        return http.get(`/?id=${id}`)
    }

    buscarTodos() {
        return http.get("/")
    }

    criar(enquete) {
        return http.post("/", enquete)
    }

    remover(id) {
        return http.delete(`/?id=${id}`)
    }
}

export default EnqueteService
